import React, { Component } from 'react';

const axios = require('axios')

class App extends Component {
    constructor(props){
        super(props);
        // this.state = {
        //     words: {}
        // }
        this.words = []
    }
    componentDidMount(){
        axios.get('http://localhost:5001/api/api')
            .then(function (response) {
                console.log(response.data.words);
                // this.setState( state => ({
                //     ...state,
                //     words: this.state.words.concat('response.data.words')
                // }))
                this.words = this.state.words.concat('response.data.words')
                // this.words = this.words.concat(response.data.words);
            })
            .catch(function (error) {
                console.log(error);
            });
    }
    render() {
        return (
            <div className="App">
                {this.words}
            </div>
        );
    }
}

export default App;
