import React, { Component } from 'react';
import { BrowserRouter, Link } from 'react-router-dom'

const axios = require('axios')

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            words: [],
            frequencyCount: [],
            sortedFC: []
        }
        this.words = [];
        this.x = ''
    }
    componentDidMount(){
        axios.get('http://localhost:5001/api/api')
            .then((response) => {
                this.setState( state => ({
                    ...state,
                    words: this.state.words.concat(response.data.words),
                    frequencyCount: this.state.frequencyCount.concat(response.data.frequencyCount),
                    sortedFC: this.state.sortedFC.concat(response.data.sortedFC)
                }))
            })
            .catch(function (error) {
                console.log(error);
            });
            this.x = 'yash'
    }

    render() {
        console.log(this.state)
        var i = 0;
        var words = this.state.sortedFC.map( w => {
            i++;
            return <p key={i}>{w[0]+" "+w[1]}</p>
        })
        return (
            <BrowserRouter>
            <div>
                <main role="main">
                    <div className="jumbotron">
                        <div className="container text-center">
                            <h1 className="display-5">Word Frequency Calculator</h1>
                            <p>A frontend to get the top N most frequently occurring words from the file <Link to="http://terriblytinytales.com/test.txt"><pre>http://terriblytinytales.com/test.txt</pre></Link></p>
                            <form class="form-inline justify-content-center">
                                <div class="form-group mr-2 mb-2">
                                    <label for="inputCount" class="sr-only">Password</label>
                                    <input type="text" class="form-control" id="inputCount" placeholder="Enter number"/>
                                </div>
                                <button type="submit" class="btn btn-primary mb-2">Submit</button>
                            </form>
                        </div>
                    </div>
                    <div className="container">
                        <div className="row">

                        </div>
                    </div>

                </main>
            </div>
            </BrowserRouter>
        );
    }
}

export default App;
