import React, { Component } from 'react';
import { BrowserRouter, Link } from 'react-router-dom'

const axios = require('axios')

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            words: [],
            frequencyCount: [],
            sortedFC: [],
            limit: 0
        }
    }
    componentDidMount(){

    }

    handleSubmitFC = (e) => {
        e.preventDefault();

        axios({
            method: 'post',
            url: 'http://localhost:5001/api/api',
            data : {
                limit: this.state.limit
            }
        })
            .then((response) => {
                this.setState( state => ({
                    ...state,
                    limit: 0,
                    words: this.state.words.concat(response.data.words),
                    sortedFC: [...response.data.sortedFC]
                }))
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        console.log(this.state)
        var i = 0;
        var words = this.state.sortedFC.map( w => {
            i++;
            return  <tr key={i}>
                        <td>{i}</td>
                        <td>{w[0]}</td>
                        <td>{w[1]}</td>
                    </tr>
        })
        return (
            <BrowserRouter>
            <div>
                <main role="main">
                    <div className="jumbotron">
                        <div className="container text-center">
                            <h1 className="display-5">Word Frequency Calculator</h1>
                            <p>A frontend to get the top N most frequently occurring words from the file</p>
                            <Link to="http://terriblytinytales.com/test.txt" target="_blank"><pre>http://terriblytinytales.com/test.txt</pre></Link>
                            <form className="form-inline justify-content-center" onSubmit={this.handleSubmitFC.bind(this)}>
                                <div className="form-group mr-2 mb-2">
                                    <label htmlFor="inputCount" className="sr-only">Password</label>
                                    <input type="text" className="form-control" id="inputCount" placeholder="Enter number" pattern="[0-9]+" title="Please enter a positive integer" onChange={(e) => this.setState({limit: e.target.value})}/>
                                </div>
                                <button type="submit" className="btn btn-primary mb-2">Submit</button>
                            </form>
                        </div>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                            {
                                words.length>0 ? (
                                    <table className="table table-bordered table-striped pb-2">
                                        <thead>
                                            <th>#</th>
                                            <th>Word</th>
                                            <th>Occurence</th>
                                        </thead>
                                        <tbody>
                                            {words}
                                        </tbody>
                                    </table>
                                ) : ( <></> )
                            }
                            </div>
                        </div>
                    </div>

                </main>
            </div>
            </BrowserRouter>
        );
    }
}

export default App;
