import React, { Component } from 'react';

const axios = require('axios')

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            words: [],
            sortedFC: [],
            limit: 0
        }
    }
    componentDidMount(){

    }

    handleSubmitFC = (e) => {
        e.preventDefault();   
        axios({
            method: 'post',
            url: 'http://localhost:5001/api/api',
            data : {
                limit: this.state.limit
            }
        })
            .then((response) => {
                this.setState( state => ({
                    ...state,
                    words: response.data.words,
                    sortedFC: response.data.sortedFC
                }))
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        console.log(this.state)
        var i = 0;
        var words = this.state.sortedFC.map( w => {
            i++;
            return  <tr key={i}>
                        <td>{i}</td>
                        <td>{w[0]}</td>
                        <td>{w[1]}</td>
                    </tr>
        })
        return (
            <div>
                <main role="main">
                    <div className="jumbotron">
                        <div className="container text-center">
                            <h1 className="display-5">Word Frequency Calculator</h1>
                            <p>A frontend to get the top N most frequently occurring words from the file</p>
                            <a href="http://terriblytinytales.com/test.txt" target="_blank" rel="noopener noreferrer"><pre>http://terriblytinytales.com/test.txt</pre></a>
                            <form className="form-inline justify-content-center" onSubmit={this.handleSubmitFC.bind(this)}>
                                <div className="form-group mr-2 mb-2">
                                    <input type="text" className="form-control" id="inputCount" placeholder="Enter number" pattern="[0-9]+" title="Please enter a positive integer" onChange={(e) => this.setState({limit: e.target.value})}/>
                                    <br/>
                                    <input className="form-check-input" type="checkbox" value="" id="defaultCheck1"/>
                                    <label className="form-check-label" htmlFor="defaultCheck1">
                                        Default checkbox
                                    </label>
                                </div>
                                <button type="submit" className="btn btn-primary mb-2">Submit</button>
                            </form>
                        </div>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                            {
                                words.length>0 ? (
                                    <table className="table table-bordered table-striped pb-2">
                                        <thead>
                                            <tr>
                                                <th>#</th>
                                                <th>Word</th>
                                                <th>Occurence</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {words}
                                        </tbody>
                                    </table>
                                ) : ( <></> )
                            }
                            </div>
                        </div>
                    </div>

                </main>
            </div>
        );
    }
}

export default App;
